import Cards from "../UI/Cards"
import './MealItem.css'


function MealItem(props){
    return(
        <Cards className="meal">
            <section></section>
            <h3>
                {props.name}
            </h3>

            <div className="description">
                {props.description}
            </div>

            <div className="price">
                {'$'+props.price}
            </div>
            
        </Cards>
        

    )
}

export default MealItem
